mapper.select:
select
  id, status, address, house_id, owner_name, tenant_name, start_time, end_time
from rental.contract


        and id = #{id}


        and status = #{status}


        and address = #{address}


        and house_id = #{houseId}


        and owner_name = #{ownerName}


        and tenant_name = #{tenantName}


        and start_time = #{startTime}


        and end_time = #{endTime}

select id, status, address, house_id, owner_name, tenant_name, start_time, end_time from rental.contract
and id like CONCAT('%',#{condition.id},'%')
and status = #{condition.status}
and address like CONCAT('%',#{condition.address},'%')
and house_id like CONCAT('%',#{condition.houseId },'%')
and owner_name like CONCAT('%',#{condition.ownerName },'%')
and tenant_name like CONCAT('%',#{condition.tenantName },'%')
and start_time >= #{condition.startTime}
and end_time <= #{condition.endTime}
and (id like CONCAT('%',#{keyWord},'%') or address like CONCAT('%',#{keyWord},'%') or house_id like CONCAT('%',#{keyWord},'%') or owner_name like CONCAT('%',#{keyWord},'%') or tenant_name like CONCAT('%',#{keyWord},'%') or start_time like CONCAT('%',#{keyWord},'%') or end_time like CONCAT('%',#{keyWord},'%'))
limit #{offset}, #{limit}
