mapper.select:
select
  id, title, content, publisher, read_num, reply_num, publish_time
from rental.questions


        and id = #{id}


        and title = #{title}


        and content = #{content}


        and publisher = #{publisher}


        and read_num = #{readNum}


        and reply_num = #{replyNum}


        and publish_time = #{publishTime}

select id, title, content, publisher, read_num, reply_num, publish_time from rental.questions
and id like CONCAT('%',#{condition.id},'%')
and title like CONCAT('%',#{condition.title},'%')
and content like CONCAT('%',#{condition.content},'%')
and publisher like CONCAT('%',#{condition.publisher},'%')
and read_num = #{condition.readNum}
and reply_num = #{condition.replyNum}
and publish_time = #{condition.publishTime}
and (id like CONCAT('%',#{keyWord},'%') or title like CONCAT('%',#{keyWord},'%') or content like CONCAT('%',#{keyWord},'%') or publisher like CONCAT('%',#{keyWord},'%'))
limit #{offset}, #{limit}
