mapper.select:
select
  id, house_id, tenan_id, ower_id, contract_id, status
from rental.rent_record


        and id = #{id}


        and house_id = #{houseId}


        and tenan_id = #{tenanId}


        and ower_id = #{owerId}


        and contract_id = #{contractId}


        and status = #{status}
