mapper.select:
select
  id, name, password, email, mobile, sex, real_name, id_card, profession, education, role, house_num, identify
from rental.user


        and id = #{id}


        and name = #{name}


        and password = #{password}


        and email = #{email}


        and mobile = #{mobile}


        and sex = #{sex}


        and real_name = #{realName}


        and id_card = #{idCard}


        and profession = #{profession}


        and education = #{education}


        and role = #{role}


        and house_num = #{houseNum}


        and identify = #{identify}

SELECT count(*) FROM rental.user
and id like CONCAT('%',#{queryCondition.id},'%')
and name like CONCAT('%',#{queryCondition.name},'%')
and password = #{queryCondition.password}
and email like CONCAT('%',#{queryCondition.email},'%')
and mobile like CONCAT('%',#{queryCondition.mobile},'%')
and sex = #{queryCondition.sex}
and real_name like CONCAT('%',#{queryCondition.email},'%')
and id_card like CONCAT('%',#{queryCondition.idCard},'%')
and profession like CONCAT('%',#{queryCondition.profession},'%')
and education like CONCAT('%',#{queryCondition.education},'%')
and role = #{queryCondition.role}
and house_num = #{queryCondition.houseNum}
and identify = #{queryCondition.identify}
and (id like CONCAT('%',#{keyWord},'%') or name like CONCAT('%',#{keyWord},'%') or email like CONCAT('%',#{keyWord},'%') or mobile like CONCAT('%',#{keyWord},'%') or real_name like CONCAT('%',#{keyWord},'%') or id_card like CONCAT('%',#{keyWord},'%') or profession like CONCAT('%',#{keyWord},'%') or education like CONCAT('%',#{keyWord},'%'))

